using Godot;using Tools;using Tools.Input;using Tools.Statemachine;using Veronenger.Game.Controller.Character;namespace Veronenger.Game.Character.Player.States {    public abstract class PlayerState : State {        protected readonly PlayerController Player;        private Logger _loggerJumpHelper;        private Logger _loggerCoyoteJump;        private Logger _loggerJumpVelocity;        public override void ConfigureLogging(StateMachine stateMachine) {            _loggerJumpHelper = LoggerFactory.GetLogger(stateMachine.Name, "JumpHelper");            _loggerCoyoteJump = LoggerFactory.GetLogger(stateMachine.Name, "CoyoteJump");            _loggerJumpVelocity = LoggerFactory.GetLogger(stateMachine.Name, "JumpVelocity");        }        // Input from the player        protected float XInput => Player.PlayerActions.LateralMotion.Strength;        protected float YInput => Player.PlayerActions.VerticalMotion.Strength;        protected ActionState Jump => Player.PlayerActions.Jump;        protected ActionState Attack => Player.PlayerActions.Attack;        protected bool IsRight => XInput > 0;        protected bool IsLeft => XInput < 0;        protected bool IsUp => YInput < 0;        protected bool IsDown => YInput > 0;        protected Vector2 Motion => Player.Motion;        protected PlayerConfig PlayerConfig => Player.PlayerConfig;        protected PlayerState(PlayerController player) {            Player = player;        }        protected void DebugJumpHelper(string message) => _loggerJumpHelper.Debug(message);        protected void DebugCoyoteJump(string message) => _loggerCoyoteJump.Debug(message);        protected void DebugJump(string message) => _loggerJumpVelocity.Debug(message);    }}